_u32 getIPByName(const char *host)
{
    _u32 IPAddr = 0;
    sl_NetAppDnsGetHostByName((_i8*)host,strlen(host),&IPAddr, SL_AF_INET);
    return IPAddr;
}

void POSIX_Init (void *args) {
    initClocksAndMemory();
    _i16 sd = 0;
    SlSockAddrIn_t Addr;
    int iAddrSize;
    _u32 hostIP;
	
    // 1. requestb.in example (big file)
    //char host[] = "requestb.in";
    //char acSendBuff [] = "POST /1l84nxp1 HTTP/1.1\r\nUser-Agent: curl/7.38.0\r\nHost: requestb.in\r\nAccept: */*\r\nContent-Length: 64009\r\nContent-Type: application/x-www-form-urlencoded\r\n\r\";
    
    // 2. learnopencv3 example, sending link to picture
    //char host[] = "learnopencv3.pythonanywhere.com";
    //char acSendBuff [] = "POST /opencvtests/default/image_dimensions/?url=http://upload.wikimedia.org/wikipedia/commons/4/4d/Ball_python_lucy.JPG HTTP/1.1\r\nHost:learnopencv3.pythonanywhere.com\r\nAccept: */*\r\n\r\n";

    // 3. SEND FILE FROM SDCARD to eottest in PythonAnywhere
    // eottest.pythonanywhere.com
    
    // 3.1 Mount SDCard and read file
    
    int mounted = SDCardMount();
    char* PathToFile = "/mnt/sdcard/0.9kb.jpg";


    //opening for obtaining the filesize
    //printf("Opening file 1 %s\n", PathToFile);
    SDCardFile* fileHandler = SDCardFileOpen(PathToFile, "r", false);
    //assert( fileHandler );	

    int payload_file_size = SDCardFileGetSize(fileHandler);

    printf("Mounted= %d , opening file of %d bytes\n", mounted, payload_file_size);

    //file variable
    //unsigned char* payload_file = (unsigned char*) calloc(payload_file_size, sizeof (char));
    unsigned char* payload_file = malloc(sizeof (unsigned char)*(payload_file_size + 1));

    int sizeInBytes = SDCardFileRead(fileHandler, payload_file, payload_file_size);

    printf("\nClosing file of %d bytes\n", sizeInBytes);
    bool boolean = SDCardFileClose(&fileHandler);
    
    // 3.2 Generate POST message including the picture
    
    char acSendBuff[10000]; 
    
    char host[] = "eottest.pythonanywhere.com";
    //first part of the POST (Content-Length = 191+payload_file_size)
    char t1[500];
    int contentLength=191+payload_file_size;
    sprintf(t1,"POST /opencvdemo/default/process_image HTTP/1.1\r\nUser-Agent: curl/7.38.0\r\nHost: eottest.pythonanywhere.com\r\nAccept: */*\r\nContent-Length: %d\r\nContent-Type: multipart/form-data; boundary=------------------------a5170f76ed5a984b\r\n\r\n--------------------------a5170f76ed5a984b\nContent-Disposition: form-data; name=\"imagen\"; filename=\"fichero_imagen.jpg\"\nContent-Type: image/jpeg\n\n",contentLength);    
    //last part of the POST
    char t3[]="\n--------------------------a5170f76ed5a984b--";
    
    //combine de POST with the file
    memcpy(acSendBuff,t1,strlen(t1));
    memcpy(acSendBuff+strlen(t1),payload_file,payload_file_size);
    memcpy(acSendBuff+strlen(t1)+payload_file_size,t3,strlen(t3));
                      
    //END
    //////////
    
    // Connect to AP and send POST message to server
    
    int retVal;
    int iTXStatus;  

    int iRXDataStatus;
    char acRecvBuff[4096] = "nothing"; 
    printf("\n");
    
    
    connectToAP("AndroidAP", "tqwg4654", SL_SEC_TYPE_WPA_WPA2, 20);

    printf("Own IP\n");
    printPrettyIPv4_u32(getOwnIP());
    hostIP = getIPByName(host);
    printPrettyIPv4_u32(hostIP);
    
    sd = sl_Socket(SL_AF_INET, SL_SOCK_STREAM, 0);
    
    Addr.sin_family = SL_AF_INET;  
	Addr.sin_port = sl_Htons(80); // default port for http  
	Addr.sin_addr.s_addr = sl_Htonl(hostIP);  
	iAddrSize = sizeof(SlSockAddrIn_t); 
    
    
    
    retVal = sl_Connect(sd, ( SlSockAddr_t *)&Addr, iAddrSize);

    iTXStatus = sl_Send(sd, acSendBuff, strlen(t1) + payload_file_size + strlen(t3), 0);

    //printf("%s",acSendBuff);
    iRXDataStatus = sl_Recv(sd, acRecvBuff, 4096, 0);


    printf("Received: %s\n", acRecvBuff);
  
    exit(0);
}


static void Fatal_extension(Internal_errors_Source  the_source,
                            bool                    is_internal,
                            uint32_t                the_error) {

    if (the_source != RTEMS_FATAL_SOURCE_EXIT)
        printk ("\nSource %d Internal %d Error %d\n", the_source, is_internal, the_error);
}
